const items = $input.all();

for (let i = 0; i < items.length; i++) {
  items[i].json.evolutionNo = i + 1;
}

return items;

const input = items;
const output = [];

// prepend loop item
output.push({
  videoNo: 0,
  from: input[input.length - 1].json,
  to: input[0].json,
});

// existing logic
for (let i = 0; i < input.length - 1; i++) {
  output.push({
    videoNo: i + 1,
    from: input[i].json,
    to: input[i + 1].json,
  });
}

return output.map(item => ({ json: item }));

// Fetch the list of species from the prompt generator
const videos = $('SPECIES PROMPT GENERATOR').all().map(x => x.json.output);
// Fetch the title, providing a default if it's not found.
const title = $('Execute').first()?.json?.title || "Evolutionary Countdown";

// Sort from oldest to newest based on the 'existed' property
videos.sort((a, b) => Number(b.existed) - Number(a.existed));

let assContent = `[Script Info]
Title: ${title}
ScriptType: v4.00+
WrapStyle: 0
PlayResX: 576
PlayResY: 1024
ScaledBorderAndShadow: yes

[V4+ Styles]
Format: Name, Fontname, Fontsize, PrimaryColour, SecondaryColour, OutlineColour, BackColour, Bold, Italic, Underline, StrikeOut, ScaleX, ScaleY, Spacing, Angle, BorderStyle, Outline, Shadow, Alignment, MarginL, MarginR, MarginV, Encoding
Style: TitleStyle,Georgia,60,&H00FFFFFF,&H000000FF,&H00000000,&H00000000,-1,0,0,0,100,100,0,0,1,2,2,5,10,10,10,1
Style: SpeciesStyle,Georgia,55,&H00FFFFFF,&H000000FF,&H00000000,&H00000000,-1,0,0,0,100,100,0,0,1,2,1,2,10,10,220,1
Style: TimeStyle,Georgia,50,&H00FFFFFF,&H000000FF,&H00000000,&H99000000,-1,0,0,0,100,100,2,0,1,0,2.5,2,10,10,160,1
Style: PeriodStyle,Georgia,45,&H00FFFFFF,&H000000FF,&H00000000,&H99000000,-1,0,0,0,100,100,1,0,1,0,2,2,10,10,100,1

[Events]
Format: Layer, Start, End, Style, Name, MarginL, MarginR, MarginV, Effect, Text
`;

function formatAssTime(seconds) {
  const h = Math.floor(seconds / 3600);
  const m = Math.floor((seconds % 3600) / 60);
  const s = Math.floor(seconds % 60);
  const cs = Math.round((seconds - Math.floor(seconds)) * 100);
  return `${h}:${String(m).padStart(2, '0')}:${String(s).padStart(2, '0')}.${String(cs).padStart(2, '0')}`;
}

function getUnitAndScale(years) {
  if (years >= 1_000_000_000) return { scale: 1_000_000_000, unit: "Billion" };
  if (years >= 1_000_000) return { scale: 1_000_000, unit: "Million" };
  if (years >= 1_000) return { scale: 1_000, unit: "Thousand" };
  return { scale: 1, unit: "" };
}

const segmentDuration = 5;
const speciesTransitionTime = 4; 
const transitionDuration = 200; 
const fps = 30;
const timeStep = 1 / fps;

// --- TITLE CARD (0s to 5s) ---
const titleStart = formatAssTime(0);
const titleEnd = formatAssTime(5);
const titleFadeEffect = `{\\fad(600, 600)}`; 
assContent += `Dialogue: 2,${titleStart},${titleEnd},TitleStyle,,0,0,0,,${titleFadeEffect}${title}\n`;

// --- PERIOD GROUPING LOGIC (Starts from Index 0) ---
if (videos.length > 0) {
    for (let i = 0; i < videos.length; i++) {
        const currentPeriod = videos[i].period;
        let endIndex = i;
        while (endIndex + 1 < videos.length && videos[endIndex + 1].period === currentPeriod) {
            endIndex++;
        }

        // Start period display at 5s + (i * 5s)
        const startTime = formatAssTime((i * segmentDuration) + 5);
        const endTime = formatAssTime((endIndex * segmentDuration) + 5 + speciesTransitionTime);

        assContent += `Dialogue: 1,${startTime},${endTime},PeriodStyle,,0,0,0,,${currentPeriod}\n`;

        // Period cross-fade logic
        if (endIndex + 1 < videos.length) {
            const nextPeriod = videos[endIndex + 1].period;
            const nextPeriodStart = formatAssTime((endIndex * segmentDuration) + 5 + speciesTransitionTime);
            const nextPeriodEnd = formatAssTime(((endIndex + 1) * segmentDuration) + 5);
            const nextPeriodEffect = `{\\fad(${transitionDuration}, 0)}`;
            assContent += `Dialogue: 1,${nextPeriodStart},${nextPeriodEnd},PeriodStyle,,0,0,0,,${nextPeriodEffect}${nextPeriod}\n`;
        }
        i = endIndex;
    }
}

// --- MAIN LOOP (Starts from Index 0, at 5 seconds) ---
let currentTime = 5;

for (let i = 0; i < videos.length; i++) {
  const from = currentTime;
  const to = from + segmentDuration;
  const species = videos[i].species;

  const speciesStart = formatAssTime(from);
  const speciesEnd = formatAssTime(from + speciesTransitionTime);

  // Fade in the very first species immediately after title (at 5s mark)
  const fadeInDuration = (i === 0) ? 500 : 0;
  const currentSpeciesEffect = `{\\fad(${fadeInDuration}, 200)}`;
  assContent += `Dialogue: 1,${speciesStart},${speciesEnd},SpeciesStyle,,0,0,0,,${currentSpeciesEffect}${species}\n`;

  // Logic for showing the upcoming species name during the last 1 second of the segment
  if (i < videos.length - 1) {
    const nextSpecies = videos[i + 1].species;
    const nextSpeciesStart = formatAssTime(from + speciesTransitionTime);
    const nextSpeciesEnd = formatAssTime(to);
    const nextSpeciesEffect = `{\\fad(${transitionDuration}, 0)}`;
    assContent += `Dialogue: 1,${nextSpeciesStart},${nextSpeciesEnd},SpeciesStyle,,0,0,0,,${nextSpeciesEffect}${nextSpecies}\n`;
  }

  // --- Timer Countdown Logic ---
  const startAbsoluteYears = videos[i].existed * 1_000_000;
  const endAbsoluteYears = (i < videos.length - 1) ? videos[i + 1].existed * 1_000_000 : startAbsoluteYears;
  const absoluteSpeed = (startAbsoluteYears - endAbsoluteYears) / segmentDuration;

  const totalSteps = segmentDuration * fps;

  for (let j = 0; j < totalSteps; j++) {
    const stepTime = j * timeStep;
    const segmentFrom = from + stepTime;
    const segmentTo = (j === totalSteps - 1) ? to : segmentFrom + timeStep;

    const eventStart = formatAssTime(segmentFrom);
    const eventEnd = formatAssTime(segmentTo);

    const currentAbsoluteYears = startAbsoluteYears - stepTime * absoluteSpeed;
    const { scale, unit } = getUnitAndScale(currentAbsoluteYears);
    const value = Math.max(1, Math.round(currentAbsoluteYears / scale));
    const yearString = Math.round(currentAbsoluteYears) === 1 ? "Year" : "Years";
    const unitString = unit ? ` ${unit}` : "";
    const timeText = `${value}${unitString} ${yearString}`;

    assContent += `Dialogue: 0,${eventStart},${eventEnd},TimeStyle,,0,0,0,,${timeText}\n`;
  }

  currentTime = to;
}

const inputUrl = $('Concat Poll').item.json.result.url;
return [{ json: { content: assContent, url: inputUrl } }];

